mapper.update:
UPDATE DLVPN
SET STATE_CD = 3
WHERE IDX = #{idx} AND USER_IDX = #{user_idx};

UPDATE DLVPN
SET IS_DEFAULT_YN = 'N'
WHERE USER_IDX = #{user_idx} and IS_DEFAULT_YN = 'Y';

UPDATE DLVPN
SET STATE_CD = 1
WHERE IDX = #{idx}

mapper.select:
SELECT ADDR_BASE, ADDR_DTL
FROM DLVPN
WHERE USER_IDX = #{user_idx} AND STATE_CD = 1;
