mapper.select:
SELECT r.*, p.NAME,p.UPLOAD_PATH
FROM (SELECT * FROM PRODUCT) p, PROD_LATE r
WHERE p.IDX = #{prod_idx} and  r.PROD_IDX = #{prod_idx}
ORDER BY r.REG_TM desc;

mapper.update:
update PROD_LATE
set CTENT = #{ctent}, OPUB_YN = #{opub_yn}, USER_IDX = #{user_idx} ,CHG_TM = now()
where idx = #{idx};
