mapper.select:
SELECT * , (select count(*) from prod_late WHERE PROD_IDX = P.IDX ) as 'review_cnt', S.CPNM FROM PRODUCT AS P INNER JOIN PROD_DTL D ON P.IDX = D.PROD_IDX AND D.REG_STATE_CD = 2 AND P.DSPLY_STATE_CD = 1 AND D.STATE_CD = 1
AND CATE_IDX LIKE CONCAT(#{category}, '%')
INNER JOIN SELER S ON S.IDX = P.SELER_IDX;

mapper.update:
UPDATE PROD_DTL
SET STATE_CD = 3
WHERE PROD_IDX = #{idx}
