mapper.update:
UPDATE SELER
SET STATE_CD = 3
WHERE IDX = #{idx}
  AND EMAIL = #{email};

UPDATE SELER
SET CPNM    = #{cpnm}
  , REPR_NM = #{repr_nm}
  , PWD     = #{pwd}
  , MPNO    = #{mpno}
  , CHG_TM  = NOW()
WHERE IDX = #{idx}
  AND EMAIL = #{email};

UPDATE SELER_DTL
SET GUID_NM    = #{guid_nm},
    GUID_EMAIL = #{guid_email},
    CLLR_NM    = #{cllr_nm},
    CLLR_TELNO = #{cllr_telno}
WHERE SELER_IDX = #{seler_idx};
