mapper.select:
select PROD_IDX, USER_IDX, REG_TM, CRT_TM, CRT_IDX, UPT_TM, UPT_IDX
from WISHLIST
where USER_IDX = ${idx}

mapper.update:
UPDATE CART
SET   PROD_CNT = PROD_CNT + #{cnt}
WHERE  PROD_IDX = ${prod_idx} and USER_IDX = ${user_idx}

mapper.select:
select count(*)
from CART
where PROD_IDX = ${prod_idx} and USER_IDX = ${user_idx}

select IDX
from USER
where EMAIL = #{email}
