mapper.select:
select PRODUCT.*
from WISHLIST , PRODUCT
where WISHLIST.PROD_IDX = PRODUCT.IDX and WISHLIST.USER_IDX = #{idx}
order by WISHLIST.REG_TM;

mapper.update:
UPDATE CART
SET   PROD_CNT = PROD_CNT + #{cnt}
WHERE  PROD_IDX = #{prod_idx} and USER_IDX = #{user_idx}

mapper.select:
select count(*)
from CART
where PROD_IDX = #{prod_idx} and USER_IDX = #{user_idx}

select IDX
from USER
where EMAIL = #{email}
